import React, { useContext, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import ListCard from './ListCard.js'
import { GlobalStoreContext } from '../store'
import DeleteModal from './DeleteModal'
/*
    This React component lists all the top5 lists in the UI.

    @author McKilla Gorilla
*/
const ListSelector = () => {
    //useContext to use globally defined variables
    const { store } = useContext(GlobalStoreContext);
    //useHistory to allow for navigating between pages
    store.history = useHistory();
    //Use the useEffect hook to update the home screen - load in all lists
    useEffect(() => {
        store.loadIdNamePairs();
    }, []);
    let listCard = "";
    if (store) {
        //if store isn't null, create a ListCard element for each list
        //the variable listCard then will contain all these ListCards to be rendered below
        listCard = store.idNamePairs.map((pair) => (
            store.currentList? 
            store.currentList._id === pair._id?
            <ListCard
                key={pair._id}
                idNamePair={pair}
                selected={true}
                editActive={store.listCreated}
            /> :
            <ListCard
            key={pair._id}
            idNamePair={pair}
            selected={false}
            editActive={store.listCreated}
            /> :
            <ListCard
            key={pair._id}
            idNamePair={pair}
            selected={false}
            editActive={store.listCreated}
             /> 
        ))
    }
    return (
        <div id="top5-list-selector">
            <div id="list-selector-heading">
                <input
                    type="button"
                    id="add-list-button"
                    className="top5-button"
                    value="+" 
                    onClick={store.createNewList}/>
                Your Lists
            </div>
            <div id="list-selector-list">
                {
                    listCard
                }
                <DeleteModal />
            </div>
        </div>)
}

export default ListSelector;
