import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
import { useHistory } from 'react-router-dom'
/*
    This toolbar is a functional React component that
    manages the undo/redo/close buttons.

    @author McKilla Gorilla
*/
function EditToolbar() {
    const { store } = useContext(GlobalStoreContext);
    const history = useHistory();

    function handleUndo() {
        if (!editStatus && !store.isBlur) {
            store.undo();
        }
    }
    function handleRedo() {
        if (!editStatus&& !store.isBlur) {
            store.redo();
        }
    }
    function handleClose() {
        if (!editStatus&& !store.isBlur) {
            history.push("/");
            store.closeCurrentList();
        }
    }
    let editStatus = store.isItemEditActive;
    let disabled = "top5-button disabled"
    let enabled = "top5-button";
    return (
        <div id="edit-toolbar">
            <div
                disabled={editStatus}
                id='undo-button'
                onClick={handleUndo}
                className={store.hasUndo ? editStatus ? disabled : enabled : disabled}>
                &#x21B6;
            </div>
            <div
                disabled={editStatus}
                id='redo-button'
                onClick={handleRedo}
                className={store.hasRedo ? editStatus ? disabled : enabled : disabled}>
                &#x21B7;
            </div>
            <div
                disabled={editStatus}
                id='close-button'
                onClick={handleClose}
                className={store.currentList ? editStatus ? disabled : enabled : disabled}>
                &#x24E7;
            </div>
        </div>
    )
}

export default EditToolbar;
